import React from 'react'
import { CCol, CRow, CFormInput, CFormLabel, CFormSelect } from '@coreui/react'

const handleEntriesChange = (event) => {
    const entriesToShow = event.target.value
    this.setState({ entriesToShow })
  }

const handleSearchChange = (event) => {
    const searchQuery = event.target.value
    this.setState({ searchQuery })
  }

const ShowEntriesSearch = ({
    entriesToShow, 
    searchQuery,
}) => {
  return (
      <CRow className="mt-2 mb-2">
        <CCol>
          <CFormLabel style={{ float: 'left' }}>Show&nbsp;</CFormLabel>
          <CFormSelect
            name="entriesToShow"
            id="entriesToShow"
            value={this.props.entriesToShow}
            onChange={handleEntriesChange}
            className="w-25"
            style={{ float: 'left' }}
            size="sm"
          >
            <option value="5">5</option>
            <option value="10">10</option>
            <option value="20">20</option>
          </CFormSelect>
        </CCol>
        <CCol>
          <CFormInput
            type="text"
            name="search"
            id="search"
            value={searchQuery}
            onChange={handleSearchChange}
            className="w-50"
            style={{ float: 'right' }}
            size="sm"
            placeholder="search"
          />
        </CCol>
      </CRow>
  )
}

export default ShowEntriesSearch
