import React from "react";
import { Row, Col, Form } from "react-bootstrap";

const ShowEntriesSearch = ({ 
    entriesToShow, 
    handleEntriesChange, 
    handleSearchChange, 
    searchQuery,
}) => {
  return (
    <Row className="mt-2 mb-2">
      <Col>
        <Form.Label style={{ float: "left" }}>Show&nbsp;</Form.Label>
        <Form.Select
          name="entriesToShow"
          id="entriesToShow"
          value={entriesToShow}
          onChange={handleEntriesChange}
          className="w-25"
          style={{ float: "left" }}
          size="sm"
        >
          <option value="5">5</option>
          <option value="10">10</option>
          <option value="20">20</option>
        </Form.Select>
      </Col>
      <Col>
        <Form.Control
          type="text"
          name="search"
          id="search"
          value={searchQuery}
          onChange={handleSearchChange}
          className="w-50"
          style={{ float: "right" }}
          size="sm"
          placeholder="search"
        />
      </Col>
    </Row>
  );
};

export default ShowEntriesSearch;
