import React, { Component } from 'react'
import { CCol, CRow, CFormInput, CFormLabel, CFormSelect } from '@coreui/react'
import PropTypes from 'prop-types'

ChildComponent.propTypes = {
  data: PropTypes.object,
}

function ChildComponent(props) {
    const data = props.data;
    console.log(data);
}

export default class ShowEntriesSearch extends Component {
  constructor(props) {
    super(props)
  }

  render() {
    const { entriesToShow } = this.props
    console.log(entriesToShow)

    return (
      <CRow className="mt-2 mb-2">
        <CCol>
          <CFormLabel style={{ float: 'left' }}>Show&nbsp;</CFormLabel>
          <CFormSelect
            name="entriesToShow"
            id="entriesToShow"
            value={entriesToShow}
            onChange={handleEntriesChange}
            className="w-25"
            style={{ float: 'left' }}
            size="sm"
          >
            <option value="5">5</option>
            <option value="10">10</option>
            <option value="20">20</option>
          </CFormSelect>
        </CCol>
        <CCol>
          <CFormInput
            type="text"
            name="search"
            id="search"
            value={searchQuery}
            onChange={handleSearchChange}
            className="w-50"
            style={{ float: 'right' }}
            size="sm"
            placeholder="search"
          />
        </CCol>
      </CRow>
    )
  }
}
