import React from 'react'
import PropTypes from 'prop-types'
import { CPagination, CPaginationItem } from '@coreui/react'

function CustomPagination(props) {
  const { entriesToShow, totalData, currentPage, setCurrentPage } = props
  const pageNumbers = []

  for (let i = 1; i <= Math.ceil(totalData / entriesToShow); i++) {
    pageNumbers.push(i)
  }

  console.log(pageNumbers)

  let start = 1,
    end = pageNumbers.length
  if (currentPage - 2 > 1) {
    start = currentPage - 2
  }
  if (currentPage + 2 < pageNumbers.length) {
    end = currentPage + 2
  }

  return (
    <CPagination aria-label="Page navigation example">
      <CPaginationItem
        aria-label="Previous"
        onClick={setCurrentPage(1)}
        disabled={currentPage === 1}
      >
        <span aria-hidden="true">&laquo;</span>
      </CPaginationItem>

      {/* {start !== 1 && <CPaginationEllipsis />} */}
      {pageNumbers.slice(start - 1, end).map((number) => (
        <CPaginationItem
          key={number}
          onClick={() => setCurrentPage(number)}
          active={currentPage === number}
        >
          {number}
        </CPaginationItem>
      ))}
      <CPaginationItem>...</CPaginationItem>
      {/* {end !== pageNumbers.length && <CPaginationEllipsis />} */}

      <CPaginationItem aria-label="Next">
        <span aria-hidden="true">&raquo;</span>
      </CPaginationItem>
    </CPagination>

    // <CPagination>
    //   <CPagination.First onClick={setCurrentPage(1)} disabled={currentPage === 1} />
    //   <CPagination.Prev
    //     onClick={() => setCurrentPage(currentPage - 1)}
    //     disabled={currentPage === 1}
    //   />
    //   {start !== 1 && <CPagination.Ellipsis />}
    //   {pageNumbers.slice(start - 1, end).map((number) => (
    //     <CPagination.Item
    //       key={number}
    //       onClick={() => setCurrentPage(number)}
    //       active={currentPage === number}
    //     >
    //       {number}
    //     </CPagination.Item>
    //   ))}
    //   {end !== pageNumbers.length && <CPagination.Ellipsis />}
    //   <CPagination.Next
    //     onClick={() => setCurrentPage(currentPage + 1)}
    //     disabled={currentPage === pageNumbers.length}
    //   />
    //   <CPagination.Last
    //     onClick={() => setCurrentPage(pageNumbers.length)}
    //     disabled={currentPage === pageNumbers.length}
    //   />
    // </CPagination>
  )
}

CustomPagination.propTypes = {
  entriesToShow: PropTypes.string,
  totalData: PropTypes.number,
  currentPage: PropTypes.number,
  setCurrentPage: PropTypes.func,
}

export default CustomPagination
